// Get funds from users
// Withdraw funds
// Set a minimum funding value in USD

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "./PriceConverter.sol"; //直接导入

// constant immutable
// 757471 gas
// 737532 gas
error NotOwner();

contract FundMe {
    using PriceConverter for uint256;

    // uint256 public number;
    uint256 public constant MINIMUM_USD = 50 * 1e18; //最小USD金额为美金计算
    //2446 gas - non-constant
    //374 gas - constant

    address[] public funders; //记录每个捐款人
    mapping(address => uint256) public addressToAmountFunded; //记录每个地址发送资金的数量

    address public immutable i_owner;
    AggregatorV3Interface public priceFeed;

    constructor(address priceFeedAddress) {
        i_owner = msg.sender;
        priceFeed = AggregatorV3Interface(priceFeedAddress);
    }

    function fund() public payable {
        // want to be able to set a minimum fund amount in USD
        // 1. How do we send ETH to this contract?
        // 如果要求至少发送 1 ether ,关键词 require 会检查 msg.value 是否大于 1
        // require(msg.value > 1e18 , "Didn't send enough!"); //1e18 == 1*10**18 == 1000000000000000000 wei == 1 ether，value单位为ETH
        require(
            msg.value.getConversionRate(priceFeed) >= MINIMUM_USD,
            "Didn't send enough!"
        ); //如何将ether转换为usd?这就是oracles的作用

        //msg.value：18 decimals
        funders.push(msg.sender);
        addressToAmountFunded[msg.sender] = msg.value;

        // What is reverting?
        // undo any action before, and send remaining gas back
        // number = 5;
        // 如果fund函数运行成功，那么number = 5，运行失败则整个函数回滚，number = 0, 消耗的gas也会原路返回
    }

    function withdraw() public onlyOwner {
        //for loop
        /* starting index, ending index, step amount */
        for (
            uint256 funderIndex = 0;
            funderIndex < funders.length;
            funderIndex++
        ) {
            address funder = funders[funderIndex];
            addressToAmountFunded[funder] = 0;
        }
        //reset the array
        funders = new address[](0);
        //actually withdraw the funds

        //1.transfer
        // msg.send = address
        //payable (msg.send) = payable address
        // payable (msg.sender).transfer(address(this).balance);

        //send
        // bool sendSuccess = payable (msg.sender).send(address(this).balance);
        // require(sendSuccess,"Send failed");

        //call
        (bool callSuccess, ) = payable(msg.sender).call{
            value: address(this).balance
        }("");
        require(callSuccess, "Send failed");
    }

    modifier onlyOwner() {
        // require(msg.sender == i_owner,"Sender id not owner!");
        if (msg.sender != i_owner) {
            revert NotOwner();
        }
        _;
    }

    //What happens if someone sends this contract ETH without calling the fund function?
    //receive()   fallback()
    receive() external payable {
        fund();
    }

    fallback() external payable {
        fund();
    }
}

contracts/PriceConverter.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol"; //直接导入

library PriceConverter {
    // 所有库中的函数都必须是internal，让库中的不同函数都可以被uint256调用

    function getPrice(
        AggregatorV3Interface priceFeed
    ) internal view returns (uint256) {
        (, int256 price, , , ) = priceFeed.latestRoundData();
        //price:BTC in terms of USD
        // 9033.148958846, remenber: priceFeed 返回的值中有八个是在小数点之后的(from function decimal:AggregatorV3Interface.sol)
        return uint256(price * 1e10); // 1e10 == 1**10 == 10000000000
    }

    function getVersion() internal view returns (uint256) {
        AggregatorV3Interface priceFeed = AggregatorV3Interface(
            0x1b44F3514812d835EB1BDB0acB33d3fA3351Ee43
        );
        return priceFeed.version();
    }

    function getConversionRate(
        uint256 ethAmount,
        AggregatorV3Interface priceFeed
    ) internal view returns (uint256) {
        uint256 ethPrice = getPrice(priceFeed);

        uint256 ethAmountInUsd = (ethPrice * ethAmount) / 1e18;
        return ethAmountInUsd;
    }
}
